#define ROM_AF_CAL_PAN_ADDR_FRONT            0x104
#define ROM_AF_CAL_MACRO_ADDR_FRONT          0x110

/* Checksum referenced section */
#define ROM_CHECKSUM_HEADER_ADDR_FRONT       0xFC
